import React, { useState } from "react";
import "./styles.css"; // Importez le fichier CSS personnalisé

const TestimonialSlider = ({ testimonials }) => {
  // ...

  return (
    <div className="testimonial-slider">
      <div className="testimonial-list grid grid-cols-1 md:grid-cols-3 gap-4">
        {displayedTestimonials.map((testimonial, index) => (
          <div key={index} className="testimonial p-4 bg-white rounded-lg shadow">
            <p className="text-gray-800">{testimonial.text}</p>
            <p className="text-gray-600">{testimonial.author}</p>
          </div>
        ))}
      </div>
      <div className="slider-controls mt-4">
        <button
          onClick={handlePrev}
          disabled={currentIndex === 0}
          className="btn btn-primary"
        >
          Précédent
        </button>
        <button
          onClick={handleNext}
          disabled={currentIndex + perPage >= testimonials.length}
          className="btn btn-primary"
        >
          Suivant
        </button>
      </div>
    </div>
  );
};
