import React, { useState } from 'react';

const testimonials = [
  {
    id: 1,
    name: 'John Doe',
    text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
  },
  {
    id: 2,
    name: 'Jane Smith',
    text: 'Suspendisse quis turpis ut odio rhoncus elementum.',
  },
  {
    id: 3,
    name: 'Alice Johnson',
    text: 'Vivamus euismod sapien ut tincidunt ultricies.',
  },
];

const TestimonialSlider = () => {
  const [currentTestimonial, setCurrentTestimonial] = useState(0);

  const nextTestimonial = () => {
    setCurrentTestimonial((currentTestimonial + 1) % testimonials.length);
  };

  const prevTestimonial = () => {
    setCurrentTestimonial(
      (currentTestimonial - 1 + testimonials.length) % testimonials.length
    );
  };

  return (
    <div className="relative py-4 mx-96 max-w-2xl">
      <div className="bg-white p-6 rounded-lg shadow-lg  h-64">
        <h2 className="text-xl font-semibold">
          {testimonials[currentTestimonial].name}
        </h2>
        <p className="mt-4 text-gray-600">
          {testimonials[currentTestimonial].text}
        </p>
      </div>
      <div className="flex justify-between items-center mt-4">
        <button
          onClick={prevTestimonial}
          className="bg-[#7B3F00] text-white px-6 py-2 rounded-full hover:shadow-md hover:bg-black duration-500"
        >
          Previous
        </button>
        <button
          onClick={nextTestimonial}
          className="bg-[#7B3F00] text-white px-6 py-2 rounded-full hover:shadow-md hover:bg-black duration-500"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default TestimonialSlider;
