import coffee1 from "./Assets/6385f858d5a2328664cd75e2_cup_of_coffee (1).png"
import coffee2 from "./Assets/6385f88f591f6fc670d5d76e_cup_of_coffee (3).png"
import coffee3 from "./Assets/6385f89b4157993b872fa1f6_cup_of_coffee (4).png"
import coffee4 from "./Assets/6385f8a7e12aa03b71fe68c3_cup_of_coffee (5).png"
import coffee5 from "./Assets/6385f8b5d5a2324a82cd7a03_cup_of_coffee (6).png"
import coffee6 from "./Assets/6385f8edd5a2324496cd7c19_cup_of_coffee (8).png"
import coffee7 from "./Assets/6385f907e50ee522170f4745_cup_of_coffee (10).png"
import "./destination.css"
import BtnSlider from "./BtnSlider"
import { useState } from 'react';
let cards = [
    {cardImg : coffee1 },
    {cardImg : coffee2 },
    {cardImg : coffee3 },
    {cardImg : coffee4 },
    {cardImg : coffee5 },
    {cardImg : coffee6 },
    {cardImg : coffee7 },
]
const Slider = () => {
    const [slideIndex, setSlideIndex] = useState(1);
    const cardsPerPage = 3; // Nombre d'images à afficher par page
  
    const nextSlide = () => {
      if (slideIndex !== Math.ceil(cards.length / cardsPerPage)) {
        setSlideIndex(slideIndex + 1);
      } else {
        setSlideIndex(1);
      }
    };
  
    const prevSlide = () => {
      if (slideIndex !== 1) {
        setSlideIndex(slideIndex - 1);
      } else {
        setSlideIndex(Math.ceil(cards.length / cardsPerPage));
      }
    };
  
    const startIndex = (slideIndex - 1) * cardsPerPage;
    const endIndex = startIndex + cardsPerPage;
    const visibleCards = cards.slice(startIndex, endIndex);
  
    return (
      <div className="destination-page">
        <div className="destination-header">
          <div className="destination-txt">
            <p className="title">TOP DESTINATION</p>
            <p className="desc">Our top value categories for you</p>
          </div>
          <div className="slide">
            <BtnSlider moveSlide={prevSlide} direction="prev"></BtnSlider>
            <BtnSlider moveSlide={nextSlide} direction="next"></BtnSlider>
          </div>
        </div>
  
        <div className="destination-cards">
          {visibleCards.map((card, index) => {
            return (
              <div
                key={index}
                className={
                  slideIndex === Math.ceil(index / cardsPerPage) + 1
                    ? "destination-card activ-dest"
                    : "destination-card"
                }
              >
                <img src={card.cardImg} alt={card.title} />
              </div>
            );
          })}
        </div>
      </div>
    );
  };
  
 
export default Slider;
